  <!-- 3. Variables CSS de color y estilos propios responsivos -->
  <style>
:root {
  /* ------------- Variables de color ------------- */
  --oc-white: #ffffff;
  --oc-white-rgb: 255, 255, 255;
  --oc-black: #000000;
  --oc-black-rgb: 0, 0, 0;

  /* ... (aquí podrías incluir todas las variables de --oc-gray-*, --oc-red-*, etc., si las necesitas) */

  /* Variables específicas de tu tema (Jekyll) */
  --color-bg: var(--oc-{{ site.color_bg  | default: 'gray-9' }});
  --color-punct: var(--oc-{{ site.color_punct | default: 'green-9' }});
  --color-key: var(--oc-{{ site.color_key | default: 'green-4' }});
  --color-value: var(--oc-{{ site.color_value | default: 'green-4' }});
  --color-hover: var(--oc-{{ site.color_hover | default: 'green-5' }});
}

/* -----------------------------------
   Estilos generales para todo el CV
   ----------------------------------- */
body {
  font-family: sans-serif;
  font-size: 100%;
  line-height: 1.5;
  background-color: var(--color-bg);
  color: var(--color-punct);
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

h1, h2, h3, h4, h5, h6 {
  color: var(--color-key);
  margin-top: 1.5rem;
  margin-bottom: 0.5rem;
}

p, li, a, span {
  color: var(--color-value);
}

/* -----------------------------------
   3.1. Bloque de imagen de perfil
   ----------------------------------- */
.perfil-container {
  width: 100%;
  max-width: 200px;         /* No exceder 200px en pantallas grandes */
  margin: 1rem auto;        /* Centrar horizontal y espacio arriba/abajo */
  text-align: center;
}

.perfil-img {
  display: block;
  width: 100%;              /* Ocupa todo el ancho del contenedor */
  height: auto;             /* Mantiene proporción (o recorta con object-fit) */
  max-width: 200px;         /* Nunca supere esta medida en desktop */
  border: 3px solid var(--color-key); /* Borde verde (mismo color-key) */
  object-fit: cover;        /* Si la foto no es exactamente cuadrada, recorta */
  border-radius: 0;         /* Asegura que no haya esquinas redondeadas */
}

/* -----------------------------------
   3.2. Zona de JSON con scroll horizontal si hace falta
   ----------------------------------- */
#json {
  width: 100%;
  overflow-x: auto;         /* Permite scroll lateral si el JSON es muy ancho */
  padding: 0 0.5rem;        /* Un poco de padding en móvil */
  box-sizing: border-box;
}

.my.ms {
  word-break: break-word;    /* Evita que cadenas largas rompan el diseño */
  padding: 0.25rem 0;
}

/* -----------------------------------
   3.3. Media Queries para móviles muy pequeños
   ----------------------------------- */
@media (max-width: 480px) {
  /* Reducir el ancho máximo de la imagen de perfil en pantallas ≤ 480px */
  .perfil-container {
    max-width: 150px;
    margin: 0.75rem auto;
  }
  .perfil-img {
    max-width: 150px;
    border-width: 2px;       /* Borde un poco más delgado en móvil estrecho */
  }
  /* Ajustar padding en bloques de JSON */
  #json {
    padding: 0 0.25rem;
  }
  /* Reducir un poco la fuente general */
  body {
    font-size: 0.9rem;       /* Aprox. 14px en pantallas muy pequeñas */
  }
}
  </style>
</head>

<body>

  <!-- 4. Bloque HTML con la foto de perfil -->
  <div class="perfil-container">
    <img
      src="{{ 'assets/images/perfil.jpg' | relative_url }}"
      alt="Foto de perfil"
      class="perfil-img"
    />
  </div>

  <!-- 5. Aquí va tu bloque JSON completo -->
  <div id="json">
    {% assign comma = site.commas == false | default: true | where_exp: "x", "x == false" | size | plus: 0 %}
    {% case site.quotes %}
      {% when 'keys' %}
        {% assign quote_key = '"' %}
        {% assign quote_value = '' %}
      {% when 'values' %}
        {% assign quote_key = '' %}
        {% assign quote_value = '"' %}
      {% when false %}
        {% assign quote_key = '' %}
        {% assign quote_value = '' %}
      {% else %}
        {% assign quote_key = '"' %}
        {% assign quote_value = '"' %}
    {% endcase %}

    {% if site.newtab == true %}
      {% assign tab = '_blank' %}
    {% else %}
      {% assign tab = '_self' %}
    {% endif %}

    {% for pair in site.data.json %}

      {%- if pair.comment -%}
        <div class="comment ms">{{ pair.comment }}</div>
        {% continue %}
      {%- endif -%}

      <div class="my ms">
        {% if pair.typed -%}
          <div id="strings">
            {% for value in pair.value %}<span>{{ value }}</span>{% endfor %}
          </div>
          {{ quote_key }}<span class="key">{{ pair.key }}</span>{{ quote_key }}:
          {{ quote_value }}<span class="value"><span id="typed"></span></span>{{ quote_value }}

        {%- elsif pair.value.first.key -%}
          {{ quote_key }}<span class="key">{{ pair.key }}</span>{{ quote_key }}: [
          {% for value in pair.value %}
            {% if value.url -%}
            <p class="ms">
              {{ quote_key }}<span class="key">{{ value.key }}</span>{{ quote_key }}:
              {{ quote_value }}<a class="value" href="{{ value.url | relative_url }}" target="{{ tab }}">{{ value.value }}</a>{{ quote_value }}{% unless forloop.last %}{{ "," }}{% endunless %}
            </p>
            {% else -%}
            <p class="ms">
              {{ quote_key }}<span class="key">{{ value.key }}</span>{{ quote_key }}:
              {{ quote_value }}<span class="value">{{ value.value }}</span>{{ quote_value }}{% unless forloop.last %}{{ "," }}{% endunless %}
            </p>
            {% endif -%}
          {% endfor %}
          ]

        {%- elsif pair.value.first -%}
          {{ quote_key }}<span class="key">{{ pair.key }}</span>{{ quote_key }}: [
          {% for value in pair.value %}
            {% if value.url -%}
            <p class="ms">{{ quote_value }}<a class="value" href="{{ value.url | relative_url }}" target="{{ tab }}">{{ value.value }}</a>{{ quote_value }}{% unless forloop.last %}{{ "," }}{% endunless %}</p>
            {% else -%}
            <p class="ms">{{ quote_value }}<span class="value">{{ value }}</span>{{ quote_value }}{% unless forloop.last %}{{ "," }}{% endunless %}</p>
            {% endif -%}
          {% endfor %}
          ]

        {%- elsif pair.url -%}
          {{ quote_key }}<span class="key">{{ pair.key }}</span>{{ quote_key }}:
          {{ quote_value }}<a class="value" href="{{ pair.url | relative_url }}" target="{{ tab }}">{{ pair.value }}</a>{{ quote_value }}

        {%- else -%}
          {{ quote_key }}<span class="key">{{ pair.key }}</span>{{ quote_key }}:
          {{ quote_value }}<span class="value">{{ pair.value }}</span>{{ quote_value }}
        {% endif %}{% unless forloop.last %}{{ "," }}{% endunless %}
      </div>

    {% endfor %}
  </div>

</body>
</html>

