// =================================================
//  Tema “VS Code Dark” + extras modo reclutador
// =================================================

// Paleta
$color_bg:    #1e1e1e;
$color_punct: #d4d4d4;
$color_key:   #9cdcfe;
$color_value: #ce9178;
$color_hover: #3794ff;

// Transformaciones
$transform_key: none;
$transform_value: none;

// =====================
//  Tipografía Hack
// =====================
@font-face {
  font-family: "hack";
  font-display: swap;
  font-style: normal;
  font-weight: 400;
  src: url("../webfonts/hack-regular-subset.woff2") format("woff2");
}

@font-face {
  font-family: "hack";
  font-display: swap;
  font-style: italic;
  font-weight: 400;
  src: url("../webfonts/hack-italic-subset.woff2") format("woff2");
}

// =====================
//  Layout principal
// =====================
html,
body { height: 100%; }

body {
  font-family: "hack", monospace;
  font-size: 16px;
  font-weight: 400;
  background: $color_bg;
  color: $color_punct;
  padding-top: 2.6rem; /* despeja la barra superior */
}

main {
  position: static;
  transform: none;
  margin: 1rem auto;
  width: 92%;
  max-width: 80ch;
  overflow-x: auto;
}

@media (min-width: 576px) { main { font-size: 12px; width: 90%; } }
@media (min-width: 768px) { main { font-size: 12px; width: 90%; } }

h1, p { margin: 0; }

a        { color: inherit; font-style: italic; }
a:hover,
a:active { color: $color_hover; }

// =====================
//  Bloque JSON
// =====================
#json {
  white-space: normal;
  word-wrap: break-word;
  overflow-x: hidden;
}

#json:before { content: "{"; }
#json:after  { content: "}"; }

.my { margin: 1rem 0; }
.mt { margin-top: 1.5rem; }
.ms { margin-left: 1.5rem; }

.key          { color: $color_key;   text-transform: $transform_key; }
.value,
.error        { color: $color_value; text-transform: $transform_value; }

// =====================
//  Cursor estilo consola
// =====================
.typed-cursor {
  font-weight: bold;
  animation: consoleBlink 1s steps(2,end) infinite;
}
@keyframes consoleBlink {
  0%,100% { opacity: 1;   color: var(--oc-green-4); }
  50%     { opacity: .25; color: var(--oc-green-6); }
}

// =====================
//  Barra superior
// =====================
#topbar {
  position: fixed;
  inset: 0 0 auto 0;
  height: 2.3rem;
  background: rgba(0,0,0,.85);
  backdrop-filter: blur(6px);
  padding: .3rem .75rem;
  display: flex;
  gap: .5rem;
  align-items: center;
  z-index: 200;
}

#topbar button,
#topbar input {
  font-family: inherit;
  font-size: .9rem;
  border: none;
  border-radius: .25rem;
  padding: .4rem .7rem;
  color: #fff;
}

#recruiter-toggle { background: var(--oc-green-7); }
#download-pdf     { background: var(--oc-green-5); display: none; }
#cv-search        {
  flex: 1 1 220px;
  background: var(--oc-gray-8);
  color: var(--oc-white);
  display: none;
}

// =====================
//  Modo reclutador
// =====================
.recruiter-mode *,
.recruiter-mode *::before,
.recruiter-mode *::after {
  animation: none !important;
  transition: none !important;
}

